import './App.css';
import {FaChevronRight, FaCheck, FaTimes} from "react-icons/fa"
import React, { useState } from 'react';

function App() {
  const [inputToDo, setInputToDo] = useState('');
  const [tab, setTab] = useState([
    {isActive:0, name:'Complete'},
    {isActive:1, name:'All'},
    {isActive:0, name:'Incomplete'}

  ])
  const [toDoList, setToDoList] = useState([
    {state:0, value:'Wake Up'}
  ])
  const onSubmit = () =>{
    if(inputToDo.trim() === '') return;
    const value = inputToDo;
    setInputToDo('');
    setToDoList([
      ...toDoList,
      {state:0, value:value}
    ])
  }
  return (
    <div className="App">
      <div className="App-header">
        <div className="card">
          <div className="title">
            To do list
          </div>
          <div className="input-form">
            <input className="input-todo" value={inputToDo} onInput={e => setInputToDo(e.target.value)} ></input>
            <div className="button-success" onClick={onSubmit}><FaChevronRight/></div>
          </div>
          <div>
            {tab.map(tab =>{
              tab.isActive ? 
              <div className="active-tab">
                {tab.name}
              </div>
              :
              <div className="desactive-tab">
                {tab.name}
              </div>
            })}
          </div>
          <div className="item-container">
            {toDoList.map(item => 
            item.state ?
                <div className="item">
                  <div>
                    {item.value}
                  </div>
                </div>
                :
                <div className="item">
                <div className="item-label">
                  {item.value}
                </div>
                <div className="select-state">
                  <div className="check-icon">
                    <FaCheck/>
                  </div>
                  <div className="times-icon">
                    <FaTimes/>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
