import './App.css';
import {FaChevronRight, FaCheck, FaTimes} from "react-icons/fa"
import React, { useState } from 'react';

function App() {
  const [inputToDo, setInputToDo] = useState('');
  const [tabs, setTabs] = useState([
    {isActive:false, name:'Complete'},
    {isActive:true, name:'All'},
    {isActive:false, name:'Incomplete'}
  ])

  const [toDoList, setToDoList] = useState([
    {state:0, value:'Wake Up'}
  ])

  const onSubmit = () => {
    if(inputToDo.trim() === '') return;
    const value = inputToDo;
    setInputToDo('');
    setToDoList([
      ...toDoList,
      {state:0, value:value}
    ]);
  }

  const onSelectTab = (event) => {
    const defaultTabs = [
    {isActive:false, name:'Complete'},
    {isActive:false, name:'All'},
    {isActive:false, name:'Incomplete'}
    ];
    const tabSelected = (element) => element.name === event.target.innerText;
    const indexTabSelected = defaultTabs.findIndex(defaultTabs.nane = tabSelected);
    defaultTabs[indexTabSelected].isActive = true;
    setTabs(defaultTabs);
  }
  return (
    <div className="App">
      <div className="App-header">
        <div className="card">
          <div className="title">
            To do list
          </div>
          <div className="input-form">
            <input className="input-todo" value={inputToDo} onInput={e => setInputToDo(e.target.value)} ></input>
            <div className="button-success" onClick={onSubmit}><FaChevronRight/></div>
          </div>
          <div className="tabs-container">
            {tabs.map(tabsItem =>
              <div className={tabsItem.isActive?'active-tab':'desactive-tab'} onClick={onSelectTab}>
                {tabsItem.name}
              </div>
            )}
          </div>
          <div className="item-container">
            {toDoList.map(item => 
            item.state ?
                <div className="item">
                  <div>
                    {item.value}
                  </div>
                </div>
                :
                <div className="item">
                <div className="item-label">
                  {item.value}
                </div>
                <div className="select-state">
                  <div className="check-icon">
                    <FaCheck/>
                  </div>
                  <div className="times-icon">
                    <FaTimes/>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
