import './App.css';
import React, { useState } from 'react';
import ToDoItem from './components/toDoItem';
import ToDoInput from './components/ToDoInput';


function App() {
  const [tabs, setTabs] = useState([
    {isActive:false, name:'Complete'},
    {isActive:true, name:'All'},
    {isActive:false, name:'Incomplete'}
  ])
  const [toDoList, setToDoList] = useState([
    {state:0, value:'Wake Up'},
    {state:1, value:'Wake Up1'},
    {state:2, value:'Wake Up1'}


  ])

  const [temporalToDoList, setTemporalToDoList] = useState(toDoList);

  const onSubmit = (toDoAdded) => {
    if(toDoAdded.trim() === '') return;
    const value = toDoAdded;
    setToDoList([
      ...toDoList,
      {state:0, value:value}
    ]);
  }

  const onSelectTab = (event) => {
    const defaultTabs = [
    {isActive:false, name:'Complete'},
    {isActive:false, name:'All'},
    {isActive:false, name:'Incomplete'}
    ];
    const tabSelected = (element) => element.name === event.target.innerText;
    const indexTabSelected = defaultTabs.findIndex(defaultTabs.nane = tabSelected);
    defaultTabs[indexTabSelected].isActive = true;
    setTabs(defaultTabs);
    refreshItemList(indexTabSelected)
  }

  const refreshItemList = (indexTabSelected) => {
    const tabSelectedAction = [
      ()=>{
        console.log('com')
        // setTemporalToDoList(() => {
        //   toDoList.filter(item => item.state == 2)
        // })
      },
      ()=>{
        console.log('all')
        // setTemporalToDoList(toDoList);
      },
      ()=>{
        console.log('inc')

        // setTemporalToDoList(() => {
        //   toDoList.filter(item => item.state == 1)
        // });
      }
    ];
      tabSelectedAction[indexTabSelected].call();
  }

  return (
    <div className="App">
      <div className="App-header">
        <div className="card">
          <ToDoInput getToDoList={onSubmit}/>
          <div className="tabs-container">
            {tabs.map(tabsItem =>
              <div className={tabsItem.isActive?'active-tab':'desactive-tab'} onClick={onSelectTab}>
                {tabsItem.name}
              </div>
            )}
          </div>
          <ToDoItem toDoList={temporalToDoList}/>
        </div>
      </div>
    </div>
  );
}

export default App;
